				$this->tile[2][img] = '/images/art1/rec/artist11_tile3.jpg';
				$this->tile[2][linkType] = 'url';
				$this->tile[2][url] = 'http://www.moneyweek.co.kr/news/mwView.php?type=1&no=2015100810088022333&outlink=1';
